<!-- Chat Widget -->
<button id="chatbot-button" class="group overflow-hidden fixed z-[999] cursor-pointer flex items-center pl-[0.6rem] bottom-[2.4rem] md:bottom-[6.4rem] right-12 md:right-[5rem] bg-red-700 text-white h-[2.5rem] w-[2.5rem] hover:w-[10rem] rounded-full border-2 border-white shadow-md shadow-slate-900 transition-all duration-700 ease-in-out">
  <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 512 512" width="18" height="18" fill="currentColor">
    <path d="M64 0C28.7 0 0 28.7 0 64L0 352c0 35.3 28.7 64 64 64l96 0 0 80c0 6.1 3.4 11.6 8.8 14.3s11.9 2.1 16.8-1.5L309.3 416 448 416c35.3 0 64-28.7 64-64l0-288c0-35.3-28.7-64-64-64L64 0z"/>
  </svg>
  <span class="absolute -right-[7rem] group-hover:right-[0.6rem] top-1/2 transform translate-y-[-50%] transition-all duration-900 ease-in-out">Chat with AIVA</span>
</button>

<button class="scroll-to-top hidden fixed bottom-[2.4rem] md:bottom-[6.4rem] right-1 md:right-[2rem] bg-yellow-600 text-white border-2 border-white size-[2.5rem] rounded-full cursor-pointer z-[9999] hover:bg-yellow-900 transition-all duration-500 ease-in-out shadow-md shadow-slate-900 hover:shadow-none hover:scale-[115%]" onclick="scrollToTop()" title="Go To Top">
  <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 320 512" width="24" height="24" fill="currentColor" class="mx-auto">
    <path d="M182.6 137.4c-12.5-12.5-32.8-12.5-45.3 0l-128 128c-9.2 9.2-11.9 22.9-6.9 34.9s16.6 19.8 29.6 19.8l256 0c12.9 0 24.6-7.8 29.6-19.8s2.2-25.7-6.9-34.9l-128-128z"/>
  </svg>
</button>
